]+\+)?(?!(?:\1|\2|\3|\4|\5|\6|\8|\9))((?:p |i |n |u |g |s |b |acl |xattrs |sha512 )\+)(?:[^#\+
]+\+)?(?!(?:\1|\2|\3|\4|\5|\6|\7|\9))((?:p |i |n |u |g |s |b |acl |xattrs |sha512 )\+)(?:[^#\+
]+\+)?(?!(?:\1|\2|\3|\4|\5|\6|\7|\8))((?:p |i |n |u |g |s |b |acl |xattrs |sha512 )\+)(?:[^#\+
]+\+)?(?!(?:\1|\2|\3|\4|\5|\6|\7|\8|\9))((?:p |i |n |u |g |s |b |acl |xattrs |sha512 ))(?:\+.*|\h*#.*|\h*)?$' l_chkout="$(grep -Pso -- "$l_regex" $l_filelist)" if [ -n "$l_chkout" ]; then l_file="$(awk -F: '{print $1}' <<< "$l_chkout")" l_pout="$(awk -F: '{print $2}' <<< "$l_chkout")" l_output="$l_output
 - Audit tool "$l_par" integrity is protected by cryptographic mechanisms
 - entry: "$l_pout"
 - exists in file: "$l_file"" else l_output2="$l_output2
 - Audit tool "$l_par" integrity is not protected by cryptographic mechanisms" fi else l_output="$l_output
 - Audit tool "$l_par" does not exist on the system. Integrity protection is not required" fi done
# If l_output2 is empty, then we pass if [ -z "$l_output2" ]; then echo -e "
- Audit Results:
 ** Pass **
$l_output" exit "${XCCDF_RESULT_PASS:-101}" else # print the reason why we are failing echo -e "
- Audit Results:
 ** Fail **
$l_output2" [ -n "$l_output" ] && echo -e "
 - Correctly configured:
$l_output" exit "${XCCDF_RESULT_FAIL:-102}" fi }

#!/usr/bin/env sh
# CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/15/20 Check existence of /etc/passwd groups in /etc/group # passing="" output="" EPG="" EGG=""
EPG=$(cut -d: -f4 /etc/passwd | uniq) EGG=$(cut -d: -f3 /etc/group | uniq) for group in $EPG; do # if ! grep -Eq "^$group$" <<< "$EGG"; then if [ -z "$(echo "$EGG" | grep -E "(^|\s)$group\b")" ]; then [ -n "$output" ] && output="$output $group" || output=$group fi done [ -z "$output" ] && passing=true # If the test passes, we pass if [ "$passing" = true ] ; then echo "All groups in /etc/passwd exist in /etc/group" exit "${XCCDF_RESULT_PASS:-101}" else # print the reason why we are failing echo "The group(s) \"$output\" exist in /etc/passwd but don't exist in /etc/group" exit "${XCCDF_RESULT_FAIL:-102}" fi
