declare
do_create boolean := true;
do_sql varchar2(1000);
begin
for privs in (select name from auditable_system_actions
where component='Standard'
and name not in ('CREATE PMEM FILESTORE','ALTER PMEM FILESTORE','DROP PMEM FILESTORE','LOAD','LOGON','LOGOFF','INSERT','UPDATE','DELETE','SELECT','ALL','CALL','EXECUTE','COMMIT','ROLLBACK','CREATE TABLE')
) loop
if do_create then
--nonPDB do_sql := 'create audit policy CIS_CDB_DDL_ACTIONS actions ' || privs.name ;
-- PDB
do_sql := 'create audit policy CIS_CDB_DDL_ACTIONS actions ' || privs.name || ' CONTAINER=ALL';
do_create := false;
else
do_sql := 'alter audit policy CIS_CDB_DDL_ACTIONS add actions ' || privs.name;
end if;
begin
execute immediate do_sql;
exception
when others then
dbms_output.put_line('skipping action "' || privs.name || '" due to: ' || SQLERRM);
end;
end loop;
end;
/
declare
-- do_create boolean := true;
do_sql varchar2(1000);
begin
for privs in (select distinct a.privilege as name from dba_sys_privs a
where a.privilege not in (select name from auditable_system_actions where component='Standard')
and privilege not in ('INHERIT ANY PRIVILEGES','SELECT ANY DICTIONARY','SELECT ANY TABLE','CREATE TABLE','CREATE ANY TABLE','CREATE SESSION')
) loop
-- if do_create then
-- do_sql := 'create audit policy CIS_CDB_DDL_ACTIONS privileges ' || privs.name ||' CONTAINER=ALL';
-- dbms_output.put_line(privs.name || ' added');
-- do_create := false;
-- else
do_sql := 'alter audit policy CIS_CDB_DDL_ACTIONS add privileges ' || privs.name;
dbms_output.put_line(privs.name || ' added');
-- end if;
begin
execute immediate do_sql;
exception
when others then
dbms_output.put_line('skipping privilege "' || privs.name || '" due to: ' || SQLERRM);
end;
end loop;
end;
/
audit policy CIS_CDB_DDL_ACTIONS;

