CREATE AUDIT POLICY CIS_CDB_LOGON_LOGOFF
ACTIONS
LOGON, LOGOFF
ACTIONS
COMPONENT=PROTOCOL HTTP, FTP, AUTHENTICATION
ONLY TOPLEVEL
CONTAINER=ALL;
AUDIT POLICY CIS_CDB_LOGON_LOGOFF;
COMMENT ON AUDIT POLICY CIS_CDB_LOGON_LOGOFF IS 'Audit policy for LOGON and LOGOFF Events';

CREATE AUDIT POLICY CIS_PDB_LOGON_LOGOFF
ACTIONS
LOGON, LOGOFF
ACTIONS
COMPONENT=PROTOCOL HTTP, FTP, AUTHENTICATION
ONLY TOPLEVEL
CONTAINER=CURRENT;
AUDIT POLICY CIS_PDB_LOGON_LOGOFF;
COMMENT ON AUDIT POLICY CIS_PDB_LOGON_LOGOFF IS 'Audit policy for LOGON and LOGOFF Events';

CREATE AUDIT POLICY CIS_CDB_EXPORT
ACTIONS
COMPONENT=DATAPUMP ALL
ACTIONS
COMPONENT=DIRECT_LOAD ALL
CONTAINER=ALL;
AUDIT POLICY CIS_CDB_EXPORT;
COMMENT ON AUDIT POLICY CIS_CDB_EXPORT IS 'Audit policy Export and direct Load';

CREATE AUDIT POLICY CIS_PDB_EXPORT
ACTIONS
COMPONENT=DATAPUMP ALL
ACTIONS
COMPONENT=DIRECT_LOAD ALL
CONTAINER=CURRENT;
AUDIT POLICY CIS_PDB_EXPORT;
COMMENT ON AUDIT POLICY CIS_PDB_EXPORT IS 'Audit policy Export and direct Load';

