ds:extended-component.sce:script:
#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # T. Harrison 8/1/18 Ensure pg_wheel group exists # shopt -s nocasematch output=$(getent group pg_wheel) group=$(grep -Po '^[^:]*pg_wheel(?=(:[^:]*)*$)' <<< $output) group_members=$(echo $output | grep -oP "^pg_wheel\:x\:\d+\:\K[\w,]+")
if [[ "$group" =~ ^[^:]*pg_wheel[^:]*$ ]] ; then echo "$group" echo "----------------------" echo "$group_members" | tr ',' '\n' shopt -u nocasematch exit "${XCCDF_RESULT_PASS:-101}" else # print the reason why we are failing echo "No 'pg_wheel' group was found." shopt -u nocasematch exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # T. Harrison 06/19/2018 Ensure FIPS 140-2 OpenSSL Cryptography Is Used # 12/06/2023 Revised to provide addtional information in # results & make the regex case insensitive #
output=$(cat /proc/sys/crypto/fips_enabled)
if [ "$output" = 1 ] ; then echo "$output" exit "${XCCDF_RESULT_PASS:-101}" else #test for fips capable system openssl_version=$(openssl version) fips_capable=$(echo $openssl_version | grep -P '(?i)(?!^OpenSSL.+-)(fips)(?=.+$)')
echo "fips_enabled = " $output echo "openssl version: " $openssl_version # print the reason why we are failing if [ -z "$fips_capable" ] ; then echo "System is not FIPS capable." else echo "System is FIPS capable, but FIPS is not enabled." fi exit "${XCCDF_RESULT_FAIL:-102}" fi
