ds:extended-component.sce:script:
#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/01/24 Check system kernel parameters # E. Pinnell 07/22/24 Modified to use more efficient check # E. Pinnell 11/21/24 Modified to work on systems without the unified filesystem
# Note: # - Allows simple regex to allow for more than one acceptable value # - Supersedes the deprecated nix_kernel_parameter_chk_v2.sh
# XCCDF_VALUE_REGEX="kernel.yama.ptrace_scope=(1|2|3)" #<- Example XCCDF_VALUE_REGEX variable
# Initialize arrays and set variables a_output=(); a_output2=(); l_ipv6_disabled="" l_ufw_sysctl_file="$([ -f /etc/default/ufw ] && awk -F= '/^\s*IPT_SYSCTL=/ {print $2}' /etc/default/ufw)" # l_analyze_cmd="$(readlink -f /lib/systemd/systemd-sysctl)" l_analyze_cmd="$(readlink -f /lib/systemd/systemd-sysctl || readlink -f /usr/lib/systemd/systemd-sysctl)"
# Function to check if IPv6 is enabled f_ipv6_chk() { l_ipv6_disabled="no" ! grep -Pqs -- '^\h*0\b' /sys/module/ipv6/parameters/disable && l_ipv6_disabled="yes" if sysctl net.ipv6.conf.all.disable_ipv6 | grep -Pqs -- "^\h*net\.ipv6\.conf\.all\.disable_ipv6\h*=\h*1\b" && \ sysctl net.ipv6.conf.default.disable_ipv6 | grep -Pqs -- "^\h*net\.ipv6\.conf\.default\.disable_ipv6\h*=\h*1\b"; then l_ipv6_disabled="yes" fi }
# Function to check kernel parameter values f_kernel_parameter_chk() { # Check kernel parameter in the running configuration l_running_parameter_value="$(sysctl "$l_parameter_name" | awk -F= '{print $2}' | xargs)" if grep -Pq -- '\b'"$l_parameter_value"'\b' <<< "$l_running_parameter_value"; then a_output+=(" - Parameter: \"$l_parameter_name\"" \ " correctly set to \"$l_running_parameter_value\" in the running configuration") else a_output2+=(" - Parameter: \"$l_parameter_name\"" \ " is incorrectly set to \"$l_running_parameter_value\" in the running configuration" \ " Should be set to: \"$l_value_out\"") fi
# Check kernel parameter value loaded from the configuration files l_used_parameter_setting="$(grep -PHs -- '^\h*'"$l_parameter_name"'\b' "$l_ufw_sysctl_file" | tail -n 1)" if [ -z "$l_used_parameter_setting" ]; then while IFS= read -r l_file; do l_file="$(tr -d '# ' <<< "$l_file")" l_used_parameter_setting="$(grep -PHs -- '^\h*'"$l_parameter_name"'\b' "$l_file" | tail -n 1)" [ -n "$l_used_parameter_setting" ] && break done < <($l_analyze_cmd --cat-config | tac | grep -Pio '^\h*#\h*\/[^#\n\r\h]+\.conf\b') fi if [ -n "$l_used_parameter_setting" ]; then while IFS=: read -r l_file_name l_file_parameter; do while IFS="=" read -r l_file_parameter_name l_file_parameter_value; do if grep -Pq -- "$l_parameter_value" <<< "$l_file_parameter_value"; then a_output+=(" - Parameter: \"${l_file_parameter_name// }\"" \ " correctly set to: \"${l_file_parameter_value// }\" in the file: \"$l_file_name\"") else a_output2+=(" - Parameter: \"${l_file_parameter_name// }\"" \ " incorrectly set to: \"${l_file_parameter_value// }\" in the file: \"$l_file_name\"" \ " Should be set to: \"$l_value_out\"") fi done <<< "$l_file_parameter" done <<< "$l_used_parameter_setting" else a_output2+=(" - Parameter: \"$l_parameter_name\" is not set in an included file" \ " *** Note: \"$l_parameter_name\" May be set in a file that's ignored by load procedure ***") fi }
# Main loop for XCCDF_VALUE_REGEX value while IFS="=" read -r l_parameter_name l_parameter_value; do # Check parameters l_parameter_name="${l_parameter_name// /}"; l_parameter_value="${l_parameter_value// /}" l_value_out="${l_parameter_value//-/ through }"; l_value_out="${l_value_out//|/ or }" l_value_out="$(tr -d '(){}' <<< "$l_value_out")" if grep -q '^net.ipv6.' <<< "$l_parameter_name"; then [ -z "$l_ipv6_disabled" ] && f_ipv6_chk if [ "$l_ipv6_disabled" = "yes" ]; then a_output+=(" - IPv6 is disabled on the system, \"$l_parameter_name\" is not applicable") else f_kernel_parameter_chk fi else f_kernel_parameter_chk fi done <<< "$XCCDF_VALUE_REGEX"
# Send test results and output to CIS-CAT if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/11/24 "Ensure mail transfer agents are configured for local-only mode" check # R. Bejar 12/11/24 Updated to include localhost
# Supersedes nix_mail_transfer_agent_local_only_chk.sh, nix_mail_transfer_agent_local_only_chk_v2.sh, and nix_mta_chk.sh
{ a_output=(); a_output2=(); a_port_list=("25" "465" "587") for l_port_number in "${a_port_list[@]}"; do if ss -plntu | grep -P -- ':'"$l_port_number"'\b' | grep -Pvq -- '\h+(127\.0\.0\.1|\[?::1\]?):'"$l_port_number"'\b'; then a_output2+=(" - Port \"$l_port_number\" is listening on a non-loopback network interface") else a_output+=(" - Port \"$l_port_number\" is not listening on a non-loopback network interface") fi done if command -v postconf &> /dev/null; then l_interfaces="$(postconf -n inet_interfaces)" elif command -v exim &> /dev/null; then l_interfaces="$(exim -bP local_interfaces)" elif command -v sendmail &> /dev/null; then l_interfaces="$(grep -i "0 DaemonPortOptions=" /etc/mail/sendmail.cr | grep -oP '?<=Addr=)[^,+]+')" fi if [ -n "$l_interfaces" ]; then if grep -Pqi '\ball\b' <<< "$l_interfaces"; then a_output2+=(" - MTA is bound to all network interfaces") elif ! grep -Pqi '(inet_interfaces\h*=\h*)?(0\.0\.0\.0|::1|loopback-only|localhost)' <<< "$l_interfaces"; then a_output2+=(" - MTA is bound to a network interface" " \"$l_interfaces\"") else a_output+=(" - MTA is not bound to a non loopback network interface" " \"$l_interfaces\"") fi else a_output+=(" - MTA not detected or in use") fi if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " * Reasons for audit failure *" "${a_output2[@]}" "" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "- Correctly set:" "${a_output[@]}" exit "${XCCDF_RESULT_FAIL:-102}" fi }

#!/usr/bin/env bash # # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # R.Bejar 12/10/24 Ensure a single time synchronization daemon is in use #
{ active_enabled_service=() services=("systemd-timesyncd.service" "chrony.service" "chronyd.service") # Determine which time synchronization daemon is in use for service in "${services[@]}"; do if systemctl is-enabled --quiet $service && systemctl is-active --quiet $service; then active_enabled_service+=("$service") fi done # Display audit results if [ ${#active_enabled_service[@]} -eq 1 ]; then printf '%s
' "" "Audit Results:" " ** PASS **" " - A single time synchronization daemon is in use follow the recommendation in ${active_enabled_service[0]} subsection ONLY" exit "${XCCDF_RESULT_PASS:-101}" elif [ ${#active_enabled_service[@]} -eq 0 ]; then printf '%s
' "" " Audit Results:" " ** FAIL **" "- No time synchronization daemon in use or unable to determine time synchronization daemon status" exit "${XCCDF_RESULT_FAIL:-102}" else printf '%s
' "" " Audit Results:" " ** FAIL **" " - Multiple services are in use: ${active_enabled_service[*]}" exit "${XCCDF_RESULT_FAIL:-102}" fi }

#!/usr/bin/env bash # # # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # rbejar 11/20/2025 SUSE Ensure a single firewall configuration utility is in use
{ active_firewall=() firewalls=("firewalld" "susefirewall2") # Determine which firewall is in use for firewall in "${firewalls[@]}"; do case $firewall in firewalld|susefirewall2) cmd=$firewall esac if command -v $cmd &> /dev/null && systemctl is-enabled --quiet $firewall && systemctl is-active --quiet $firewall; then active_firewall+=("$firewall") fi done # Display audit results if [ ${#active_firewall[@]} -eq 1 ]; then printf '%s
' "" "Audit Results:" " ** PASS **" " - A single firewall is in use follow the recommendation in ${active_firewall[0]} subsection ONLY" exit "${XCCDF_RESULT_PASS:-101}" elif [ ${#active_firewall[@]} -eq 0 ]; then printf '%s
' "" " Audit Results:" " ** FAIL **" "- No firewall in use or unable to determine firewall status" exit "${XCCDF_RESULT_FAIL:-102}" else printf '%s
' "" " Audit Results:" " ** FAIL **" " - Multiple firewalls are in use: ${active_firewall[*]}" exit "${XCCDF_RESULT_FAIL:-102}" fi }

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/31/24 openSSH private key file mode/owner/group check version 4 #
# ** This will only work on newer systems. ** # Note: # (supersedes nix_ssh_private_key_perm_chk_v2 and nix_ssh_private_key_perm_chk_v2) # Requires updated file command, but is an improved check where the newer command is available # needs to be validated for Fedora 28+ based distributions (Not applicable for Fedora 19)
a_output=(); a_output2=() l_ssh_group_name="$(awk -F: '($1 ~ /^(ssh_keys|_?ssh)$/) {print $1}' /etc/group)" f_file_chk() { while IFS=: read -r l_file_mode l_file_owner l_file_group; do a_out2=() [ "$l_file_group" = "$l_ssh_group_name" ] && l_pmask="0137" || l_pmask="0177" l_maxperm="$( printf '%o' $(( 0777 & ~$l_pmask )) )" if [ $(( $l_file_mode & $l_pmask )) -gt 0 ]; then a_out2+=(" Mode: \"$l_file_mode\" should be mode: \"$l_maxperm\" or more restrictive") fi if [ "$l_file_owner" != "root" ]; then a_out2+=(" Owned by: \"$l_file_owner\" should be owned by \"root\"") fi if [[ ! "$l_file_group" =~ ($l_ssh_group_name|root) ]]; then a_out2+=(" Owned by group \"$l_file_group\" should be group owned by: \"$l_ssh_group_name\" or \"root\"") fi if [ "${#a_out2[@]}" -gt "0" ]; then a_output2+=(" - File: \"$l_file\"${a_out2[@]}") else a_output+=(" - File: \"$l_file\"" \ " Correct: mode: \"$l_file_mode\", owner: \"$l_file_owner\" and group owner: \"$l_file_group\" configured") fi done < <(stat -Lc '%#a:%U:%G' "$l_file") }
# Loop to find private openSSH key files (Note: this requires the newer file command) while IFS= read -r -d $'\0' l_file; do if ssh-keygen -lf &>/dev/null "$l_file"; then file "$l_file" | grep -Piq -- '\bopenssh\h+([^#\n\r]+\h+)?private\h+key\b' && f_file_chk fi done < <(find -L /etc/ssh -xdev -type f -print0 2>/dev/null)
# Send test results and assessment evidence to CIS-CAT if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/31/24 Check openSSH public key file(s) access #
# ** This will only work on newer systems. ** # Note: # version 4 (supersedes nix_ssh_private_key_perm_chk_v2 and nix_ssh_private_key_perm_chk_v3) # Requires updated file command, but is an improved check where the newer command is available # needs to be validated for Fedora 28+ based distributions (Not applicable for Fedora 19)
a_output=(); a_output2=() l_pmask="0133"; l_maxperm="$( printf '%o' $(( 0777 & ~$l_pmask )) )" f_file_chk() { while IFS=: read -r l_file_mode l_file_owner l_file_group; do a_out2=() if [ $(( $l_file_mode & $l_pmask )) -gt 0 ]; then a_out2+=(" Mode: \"$l_file_mode\" should be mode: \"$l_maxperm\" or more restrictive") fi if [ "$l_file_owner" != "root" ]; then a_out2+=(" Owned by: \"$l_file_owner\" should be owned by: \"root\"") fi if [ "$l_file_group" != "root" ]; then a_out2+=(" Owned by group \"$l_file_group\" should be group owned by group: \"root\"") fi if [ "${#a_out2[@]}" -gt "0" ]; then a_output2+=(" - File: \"$l_file\"" "${a_out2[@]}") else a_output+=(" - File: \"$l_file\"" \ " Correct: mode: \"$l_file_mode\", owner: \"$l_file_owner\" and group owner: \"$l_file_group\" configured") fi done < <(stat -Lc '%#a:%U:%G' "$l_file") }
# Loop to find public openSSH key files (Note: this requires the newer file command) while IFS= read -r -d $'\0' l_file; do if ssh-keygen -lf &>/dev/null "$l_file"; then file "$l_file" | grep -Piq -- '\bopenssh\h+([^#\n\r]+\h+)?public\h+key\b' && f_file_chk fi done < <(find -L /etc/ssh -xdev -type f -print0 2>/dev/null)
# Send test results and assessment evidence to CIS-CAT if [ "${#a_output2[@]}" -le 0 ]; then [ "${#a_output[@]}" -le 0 ] && a_output+=(" - No openSSH public keys found") printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/23/24 Check sshd configuration parameter match # E. Pinnell 08/26/24 Modified to account for edge case with Ubuntu Workstation not creating a directory on boot # E. Pinnell 10/03/24 Modified to work with SLES
# Note: # - Replaces deprecated: sshd_running_config.sh, nix_sshd_config_chk.sh, nix_sshd_config_not_match.sh # - Provides a much improved response time vs the now deprecated nix_sshd_config_chk.sh and nix_sshd_config_not_match.sh scripts # - Variable XCCDF_VALUE_REGEX: # - is a three part colon separated variable (Parameter: MaxStartups is a "special" case. See example below) # - is made up as "{OPTION}:{OPERATOR}:{OPTION_VALUE}" # - OPERATORS: # - match <- Pass if the set option value matches a string or regex in the {OPTION_VALUE} field # - not_match <- Pass if the set option value does not match a string or regex in the {OPTION_VALUE} field # - lt <- Pass if the set option value is less than the integer in the {OPTION_VALUE} field # - le <- Pass if the set option value is less than to equal to the integer in the {OPTION_VALUE} field # - gt <- Pass if the set option value is greater than the integer in the {OPTION_VALUE} field # - ge <- Pass if the set option value is greater than or equal to the integer in the {OPTION_VALUE} field # - Examples included below # XCCDF_VALUE_REGEX="MaxStartups::10:30:60" # <- example complex XCCDF_VALUE_REGEX variable # XCCDF_VALUE_REGEX="kexalgorithms:not_match:diffie-hellman-group1-sha1,diffie-hellman-group14-sha1,diffie-hellman-group-exchange-sha1,diffie-hellman-group-exchange-sha256" # XCCDF_VALUE_REGEX="allowusers,allowgroups,denyusers,denygroups:match:.+$" # XCCDF_VALUE_REGEX="banner:match:\/\H+"
# Initialize arrays and set variables a_output=(); a_output2=(); a_output3=() l_sshd_cmd="$(readlink -e /usr/sbin/sshd)" [ ! -f "$l_sshd_cmd" ] && l_sshd_cmd="$(whereis sshd | awk '{print $2}' | xargs)" [ ! -f "$l_sshd_cmd" ] && a_output2+=(" - The sshd command can not be found on the system") l_var="$("$l_sshd_cmd" -t 2>&1 | grep -Pvi -- '\bterminating\b')"
# Check Functions # Function to check string matching options f_regex_match() { while IFS= read -r l_match_option_name; do l_match_option="${l_match_option_name,,}" l_match_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_match_option"'$/) print tolower($2)}')" if [ -n "$l_match_config_value" ]; then if grep -Pqsi -- '\b('"$l_value"')\b' <<< "$l_match_config_value"; then a_output+=(" - Option: \"$l_match_option_name\" correctly set to: \"$l_match_config_value\"") else a_output2+=(" - Option(s): \"$l_match_option_name\" incorrectly set to: \"$l_match_config_value\"" \ " Should be set to: \"${l_value//|/ or }\"") fi fi done < <(echo -e "${l_option_name//,/\\n}") }
# Function to check string not match options (cyphers, MACs, key exchange, etc) f_regex_not_match() { a_wrong_value=(); while IFS= read -r l_wrong_value; do grep -Psiq -- '\b'"$l_wrong_value"'\b' <<< "$l_config_value" && a_wrong_value+=("$l_wrong_value") done < <(echo -e "${l_value//|/\\n}") if [ "${#a_wrong_value[@]}" -gt "0" ]; then l_wrong_out="${a_wrong_value[*]}" && a_output2+=(" - Option: \"$l_option_name\" incorrectly includes the following value(s):" \ " ${l_wrong_out// /, }") else a_output+=(" - Option: \"$l_option_name\" does not include and of the following incorrect values:" \ " ${l_value//|/, }") fi }
# Function to check integers f_integer_chk() { l_test="" case "$l_operator" in lt ) l_int_op_display="less than" [ "$l_config_value" -lt "$l_value" ] && l_test="pass" ;; le ) l_int_op_display="less than or equal to" [ "$l_config_value" -le "$l_value" ] && l_test="pass" ;; gt ) l_int_op_display="greater than" [ "$l_config_value" -gt "$l_value" ] && l_test="pass" ;; ge ) l_int_op_display="greater than or equal to" [ "$l_config_value" -ge "$l_value" ] && l_test="pass" ;; * ) l_int_op_display="equal to" [ "$l_config_value" = "$l_value" ] && l_test="pass" ;; esac if [ "$l_test" = "pass" ]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") fi }
# Function to check "MaxStartups" option f_maxstartups_chk() { while IFS=: read -r _ _ l_value1 l_value2 l_value3; do while IFS=: read -r l_config_value1 l_config_value2 l_config_value3; do if [[ "$l_config_value1" -le "$l_value1" && "$l_config_value2" -le "$l_value2" && "$l_config_value3" -le "$l_value3" ]]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"" \ " Should be set to: \"$l_value1:$l_value2:$l_value3\" or lower values") fi done <<< "$l_config_value" done <<< "$XCCDF_VALUE_REGEX" }
# Main script if grep -Piq -- '\b(bad|unsupported)\b' <<< "$l_var"; then if sshd -T 2>&1 | grep -Pqsi '\bterminating\b'; then a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var" \ " - Unable to test configuration") else a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var") fi elif sshd -T 2>&1 | grep -Piq -- '\bMissing\h+privilege\h+separation\h+directory\b'; then a_output2+=(" - openSSH server hasn't fully started correctly" \ " Please run \"systemctl restart ssh\"" " and re-scan the system") else [ -n "$l_var" ] && a_output3+=(" ** WARNING **" " - $l_var") # Set hostname and hast address variables for sshd -T command if needed if sshd -T 2>&1 | grep -Pqi -- '\bmatch\h+group\b'; then l_hostname="$(hostname)" l_host_address="$(hostname -I | cut -d ' ' -f1)" l_sshd_command="$l_sshd_cmd -T -C user=root -C host=$l_hostname -C addr=$l_host_address" else l_sshd_command="$l_sshd_cmd -T" fi while IFS=: read -r l_option_name l_operator l_value; do l_option="${l_option_name,,}"; l_value="${l_value//,/|}" grep -q -- ',' <<< "$l_option" && l_option="(${l_option//,/|})" l_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_option"'$/) print tolower($2)}')" if [ -n "$l_config_value" ]; then if [ "$l_option" = "maxstartups" ]; then f_maxstartups_chk else case "$l_operator" in match ) f_regex_match ;; not_match ) f_regex_not_match ;; * ) f_integer_chk ;; esac fi else a_output2+=(" - Parameter(s): \"${l_option_name//,/, }\"" " does not exist in the openSSH daemon configuration") fi done <<< "$XCCDF_VALUE_REGEX" fi
# Send check results and output to CIS-CAT if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" "${a_output3[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" "${a_output3[@]}" "" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/30/24 Check sshd configuration match block check # E. Pinnell 10/03/24 Modified to work with SLES
# Note: # - Variable XCCDF_VALUE_REGEX: # - is a three part colon separated variable (Parameter: MaxStartups is a "special" case. See example below) # - is made up as "{OPTION}:{OPERATOR}:{OPTION_VALUE}" # - OPERATORS: # - match <- Pass if the set option value matches a string or regex in the {OPTION_VALUE} field # - not_match <- Pass if the set option value does not match a string or regex in the {OPTION_VALUE} field # - lt <- Pass if the set option value is less than the integer in the {OPTION_VALUE} field # - le <- Pass if the set option value is less than to equal to the integer in the {OPTION_VALUE} field # - gt <- Pass if the set option value is greater than the integer in the {OPTION_VALUE} field # - ge <- Pass if the set option value is greater than or equal to the integer in the {OPTION_VALUE} field # - Examples included below # XCCDF_VALUE_REGEX="MaxStartups::10:30:60" # <- example complex XCCDF_VALUE_REGEX variable # XCCDF_VALUE_REGEX="kexalgorithms:not_match:diffie-hellman-group1-sha1,diffie-hellman-group14-sha1,diffie-hellman-group-exchange-sha1,diffie-hellman-group-exchange-sha256" # XCCDF_VALUE_REGEX="allowusers,allowgroups,denyusers,denygroups:match:.+$" # XCCDF_VALUE_REGEX="banner:match:\/\H+" # XCCDF_VALUE_REGEX="loglevel:match:verbose,info"
a_output=(); a_output2=(); a_output3=() l_file="/etc/ssh/sshd_config"; a_sshd_config_files=("$l_file"); a_unchecked_files=("$l_file") l_sshd_cmd="$(readlink -e /usr/sbin/sshd)" [ ! -f "$l_sshd_cmd" ] && l_sshd_cmd="$(whereis sshd | awk '{print $2}' | xargs)" [ ! -f "$l_sshd_cmd" ] && a_output2+=(" - The sshd command can not be found on the system") l_var="$("$l_sshd_cmd" -t 2>&1 | grep -Psvi -- '\bterminating\b')"
f_sshd_chk() { # Check Functions # Function to check string matching options f_regex_match() { while IFS= read -r l_match_option_name; do l_match_option="${l_match_option_name,,}" l_match_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_match_option"'$/) print tolower($2)}')" if [ -n "$l_match_config_value" ]; then if grep -Pqsi -- '\b('"$l_value"')\b' <<< "$l_match_config_value"; then [ "${#a_output[@]}" -le "0" ] && \ a_output+=(" - Option: \"$l_match_option_name\" correctly set to: \"$l_match_config_value\"") else a_output2+=(" - Option(s): \"$l_match_option_name\" incorrectly set to: \"$l_match_config_value\"" \ " In the file: \"$l_sshd_config_file\"" " Should be set to: \"${l_value//|/ or }\"") fi fi done < <(echo -e "${l_option_name//,/\\n}") }
# Function to check string not match options (cyphers, MACs, key exchange, etc) f_regex_not_match() { a_wrong_value=(); while IFS= read -r l_wrong_value; do grep -Psiq -- '\b'"$l_wrong_value"'\b' <<< "$l_config_value" && a_wrong_value+=("$l_wrong_value") done < <(echo -e "${l_value//|/\\n}") if [ "${#a_wrong_value[@]}" -gt "0" ]; then l_wrong_out="${a_wrong_value[*]}" && a_output2+=(" - Option: \"$l_option_name\" incorrectly includes the following value(s):" \ " ${l_wrong_out// /, }" " In the file: \"$l_sshd_config_file\"") else [ "${#a_output[@]}" -le "0" ] && \ a_output+=(" - Option: \"$l_option_name\" does not include and of the following incorrect values:" " ${l_value//|/, }") fi }
# Function to check integers f_integer_chk() { l_test="" case "$l_operator" in lt ) l_int_op_display="less than" [ "$l_config_value" -lt "$l_value" ] && l_test="pass" ;; le ) l_int_op_display="less than or equal to" [ "$l_config_value" -le "$l_value" ] && l_test="pass" ;; gt ) l_int_op_display="greater than" [ "$l_config_value" -gt "$l_value" ] && l_test="pass" ;; ge ) l_int_op_display="greater than or equal to" [ "$l_config_value" -ge "$l_value" ] && l_test="pass" ;; * ) l_int_op_display="equal to" [ "$l_config_value" = "$l_value" ] && l_test="pass" ;; esac if [ "$l_test" = "pass" ]; then [ "${#a_output[@]}" -le "0" ] && \ a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value\"" \ " In the file: \"$l_sshd_config_file\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") fi }
# Function to check "MaxStartups" option f_maxstartups_chk() { while IFS=: read -r _ _ l_value1 l_value2 l_value3; do while IFS=: read -r l_config_value1 l_config_value2 l_config_value3; do if [[ "$l_config_value1" -le "$l_value1" && "$l_config_value2" -le "$l_value2" && "$l_config_value3" -le "$l_value3" ]]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"" \ " In the file: \"$l_sshd_config_file\"" \ " Should be set to: \"$l_value1:$l_value2:$l_value3\" or lower values") fi done <<< "$l_config_value" done <<< "$XCCDF_VALUE_REGEX" }
# Main script for f_sshd_chk [ -n "$l_var" ] && a_output3+=(" ** WARNING **" " - $l_var") while IFS=: read -r l_option_name l_operator l_value; do l_option="${l_option_name,,}"; l_value="${l_value//,/|}"; # l_value="${l_value,,}" grep -qs -- ',' <<< "$l_option" && l_option="(${l_option//,/|})" l_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_option"'$/) print tolower($2)}')" if [ -n "$l_config_value" ]; then if [ "$l_option" = "maxstartups" ]; then f_maxstartups_chk else case "$l_operator" in match ) f_regex_match ;; not_match ) f_regex_not_match ;; * ) f_integer_chk ;; esac fi else a_output2+=(" - Parameter(s): \"${l_option_name//,/, }\"" " does not exist in the openSSH daemon configuration") fi done <<< "$XCCDF_VALUE_REGEX" } # End of f_sshd_chk
f_add_sshd_config_files() { while IFS= read -r -d $'\0' l_file; do if [ -f "$(readlink -f "$l_file")" ]; then a_sshd_config_files+=("$(readlink -f "$l_file")") a_unchecked_files+=("$(readlink -f "$l_file")") fi done < <(find -L "$l_directory" -name "$l_ext" -type f -print0) f_check_unchecked }
f_match_value_chk() { while IFS=, read -r l_value; do l_sshd_command="$l_sshd_cmd -T -C $l_match_type=$l_value" f_sshd_chk done <<< "$l_match_value" }
f_add_include_files() { while IFS=' ' read -r l_include_location; do # Lets work out the default include if set if ! grep -Psiq -- '[*]' <<< "$l_include_location"; then if [ -f "$(readlink -f "$l_include_location")" ]; then l_include_location="$(readlink -f "$l_include_location")" ! grep -Psiq '\b'"$l_include_location"'\b' "${a_sshd_config_files[*]}" && a_sshd_config_files+=("${l_include_location// }") ! grep -Psiq '\b'"$l_include_location"'\b' "${a_unchecked_files[*]}" && a_unchecked_files+=("${l_include_location// }") f_check_unchecked elif [ -d "$(readlink -f "$l_include_location")" ]; then l_ext='*'; l_directory="$(readlink -f "$l_include_location")"; f_add_sshd_config_files f_check_unchecked elif [ -f "/etc/ssh/$l_include_location" ]; then l_include_location="$(readlink -f /etc/ssh/$l_include_location)" a_sshd_config_files+=("/etc/ssh/${l_include_location// }"); a_unchecked_files+=("${l_include_location// }") f_check_unchecked fi else l_directory="$(readlink -f "$(dirname "$l_include_location")")" l_ext="$(basename "$l_include_location")" f_add_sshd_config_files fi done < <(awk '{IGNORECASE=1; if ($1~/^\s*include$/) print $2}' "$l_unchecked_file" 2>/dev/null) [ "${#a_unchecked_files[@]}" -gt "0" ] && f_check_unchecked }
f_check_unchecked() { for l_unchecked_file in "${a_unchecked_files[@]}"; do a_unchecked_files=("${a_unchecked_files[@]/$l_unchecked_file}") ! grep -Psq '\/' "${a_unchecked_files[*]}" && a_unchecked_files=() grep -Pqsi -- '\h*^include\b' "$l_unchecked_file" && f_add_include_files done }
# Main Script if grep -Psiq -- '\b(bad|unsupported)\b' <<< "$l_var"; then if sshd -T 2>&1 | grep -Pqsi '\bterminating\b'; then a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var" \ " - Unable to test configuration") else a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var") fi else [ -n "$l_var" ] && a_output3+=(" ** WARNING **" " - $l_var") f_check_unchecked for l_sshd_config_file in "${a_sshd_config_files[@]}"; do while IFS=" " read -r _ l_match l_match_value; do a_output3+=(" - File: \"$l_sshd_config_file\" Includes Match Block for:" " Match $l_match $l_match_value") case "${l_match,,}" in user ) l_match_type="user" f_match_value_chk ;; group ) l_match_type="group" f_match_value_chk ;; host ) l_match_type="host" f_match_value_chk ;; localaddress ) l_match_type="laddr" l_match_value="$(sed 's/\/[0-9]*//g' <<< "$l_match_value")" f_match_value_chk ;; localport ) l_match_type="lport" f_match_value_chk ;; address ) l_match_type="addr" l_match_value="$(sed 's/\/[0-9]*//g' <<< "$l_match_value")" f_match_value_chk ;; * ) a_output2+=(" - Unable to test Match in file: \"$l_sshd_config_file\"" " Please review manually") ;; esac done < <(awk '{IGNORECASE=1; if ($1~/^\s*match$/) print $0}' "$l_sshd_config_file" 2>/dev/null) done fi
# Send check results and output to CIS-CAT if [ "${#a_output[@]}" -le 0 ] && [ "${#a_output2[@]}" -le 0 ]; then a_output+=(" - No Match Blocks found in the openSSH daemon configuration file(s)") fi
if [ "${#a_output2[@]}" -le 0 ]; then [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" " * Note: *" "${a_output3[@]}" printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" "${a_output3[@]}" printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 08/12/24 Check root account access is controlled # E. Pinnell 08/28/24 Modified to work with Fedora based distributions and improve evidence output
a_output=() a_output2=() a_output3=() l_user="root"
f_report_hash() { l_found_value="$(awk -F: '($1=="'"$l_user"'") {split($2,a,"$"); print a[2]}' /etc/shadow)" case "${l_found_value,,}" in 1 ) l_hash="MD5" ;; 2a ) l_hash="blowfish" ;; 5 ) l_hash="SHA-256" ;; 6 ) l_hash="SHA-512" ;; y ) l_hash="yescrypt" ;; * ) l_hash="DES" ;; esac }
l_account_status="$(passwd -S "$l_user" | awk '{print $2}')" if [ -n "$l_account_status" ]; then case "$l_account_status" in NP ) a_output2+=(" - User account: \"$l_user\" has no password and is not locked" \ " Please set a password or lock the account") ;; L|LK ) a_output+=(" - User account: \"$l_user\" is locked") ;; P|PS ) f_report_hash ! grep -Psq -- '(SHA-512|yescrypt)' <<< "$l_hash" && a_output3+=(" ** WARNING **" \ " User account: \"$l_user\" password" \ " is using the \"$l_hash\" hashing algorithm") a_output+=(" - User account: \"$l_user\" password has been set" \ " using the \"$l_hash\" hashing algorithm") ;; esac else a_output2+=(" - User account: \"$l_user\" has no password" \ " Please set a password or lock the account") fi
# Send test results and assessment evidence to CIS-CAT [ "${#a_output3[@]}" ] && printf '%s\n' "" "${a_output3[@]}" if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 06/05/24 Ensure TMOUT is set and readonly
# Supersedes deprecated "tmout_chk.sh" # XCCDF_VALUE_REGEX="900" #<- Sample XCCDF_VALUE_REGEX
a_output=(); a_output2=() # Initialize output arrays
# Function to check if TMOUT is read only and exported f_tmout_read_chk() { a_out=(); a_out2=() l_tmout_readonly="$(grep -P -- '^\h*(typeset\h\-xr\hTMOUT=\d+|([^#
]+)? readonly\h+TMOUT )' "$l_file")" l_tmout_export="$(grep -P -- '^\h*(typeset\h\-xr\hTMOUT=\d+|([^#
]+)? export ([^#
]+ )?TMOUT )' "$l_file")" if [ -n "$l_tmout_readonly" ]; then a_out+=(" - Readonly is set as: "$l_tmout_readonly" in: "$l_file"") else a_out2+=(" - Readonly is not set in: "$l_file"") fi if [ -n "$l_tmout_export" ]; then a_out+=(" - Export is set as: "$l_tmout_export" in: "$l_file"") else a_out2+=(" - Export is not set in: "$l_file"") fi }
# Loop to find files containing TMOUT while IFS= read -r l_file; do l_tmout_value="$(grep -Po -- '^([^#
]+)? TMOUT=\d+ ' "$l_file" | awk -F= '{print $2}')" f_tmout_read_chk if [ -n "$l_tmout_value" ]; then if [[ "$l_tmout_value" -le "$XCCDF_VALUE_REGEX" && "$l_tmout_value" -gt "0" ]]; then a_output+=(" - TMOUT is set to: "$l_tmout_value" in: "$l_file"") [ "${#a_out[@]}" -gt 0 ] && a_output+=("${a_out[@]}") [ "${#a_out2[@]}" -gt 0 ] && a_output2+=("${a_out[@]}") fi if [[ "$l_tmout_value" -gt "$XCCDF_VALUE_REGEX" || "$l_tmout_value" -le "0" ]]; then a_output2+=(" - TMOUT is incorrectly set to: "$l_tmout_value" in: "$l_file"") [ "${#a_out[@]}" -gt 0 ] && a_output2+=(" ** Incorrect TMOUT value **" "${a_out[@]}") [ "${#a_out2[@]}" -gt 0 ] && a_output2+=("${a_out2[@]}") fi else [ "${#a_out[@]}" -gt 0 ] && a_output2+=(" - TMOUT is not set" "${a_out[@]}") [ "${#a_out2[@]}" -gt 0 ] && a_output2+=(" - TMOUT is not set" "${a_out2[@]}") fi done < <(grep -Pls -- '^([^#
]+)? TMOUT ' /etc/*bashrc /etc/profile /etc/profile.d/*.sh)
# Check for PASS/FAIL status and product output report [[ "${#a_output[@]}" -le 0 && "${#a_output2[@]}" -le 0 ]] && a_output2+=(" - TMOUT is not configured") if [ "${#a_output2[@]}" -le 0 ]; then printf '%s
' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s
' "" "- Audit Result:" " ** FAIL **" " * Reasons for audit failure *" "${a_output2[@]}" "" [ "${#a_output[@]}" -gt 0 ] && printf '%s
' "- Correctly set:" "${a_output[@]}" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 06/17/24 Check cryptographic mechanisms are used to protect the integrity of audit tools # E. Pinnell 11/15/24 Modified to replace whereis with more reliable test
# Note: supersedes deprecated nix_audit_tool_aide_chk_v2.sh
# Setup output arrays, auditd tools to look for a_output=();a_output2=();a_output3=();a_parlist=() l_systemd_analyze="$(whereis systemd-analyze | awk '{print $2}')" a_audit_files=("auditctl" "auditd" "ausearch" "aureport" "autrace" "augenrules")
# Function to check if all options a included f_parameter_chk() { for l_tool_file in "${a_parlist[@]}"; do if grep -Pq -- ' '"$l_tool_file"' ' <<< "${!A_out[*]}"; then for l_string in "${!A_out[@]}"; do l_check="$(grep -Po -- '^\h*(/usr)?/sbin/'"$l_tool_file"' ' <<< "$l_string")" if [ -n "$l_check" ]; then l_fname="$(printf '%s' "${A_out[$l_string]}")" [ "$l_check" != "$(readlink -f "$l_check")" ] && \ a_output3+=(" - "$l_check" should be updated to: "$(readlink -f "$l_check")"" " in: "$l_fname"") a_missing=() for l_var in "${a_items[@]}"; do if ! grep -Pq -- " $l_var " <<< "$l_string"; then a_missing+=(""$l_var"") fi done if [ "${#a_missing[@]}" -gt 0 ]; then a_output2+=(" - Option(s): ( ${a_missing[*]} ) are missing from: "$l_tool_file" in: "$l_fname"") else a_output+=(" - Audit tool file "$l_tool_file" exists as:" " "$l_string"" " in the configuration file: "$l_fname"") fi fi done else a_output2+=(" - Audit tool file "$l_tool_file" doesn't exist in an AIDE configuration file") fi done }
# Function to create array with "winning" configuration information f_aide_conf() { # l_config_file="$(whereis aide.conf | awk '{print $2}')" l_config_file="$(readlink -f /etc/aide/aide.conf)" || l_config_file="/etc/aide.conf" if [ -f "$l_config_file" ]; then a_items=("p" "i" "n" "u" "g" "s" "b" "acl" "xattrs" "sha512") declare -A A_out while IFS= read -r l_out; do if grep -Pq -- '^\h*\#\h*/[^#
]+\.conf ' <<< "$l_out"; then l_file="${l_out//# /}" else for i in "${a_parlist[@]}"; do grep -Pq -- '^\h*(/usr)?/sbin/'"$i"' ' <<< "$l_out" && A_out+=(["$l_out"]="$l_file") done fi done < <("$l_systemd_analyze" cat-config "$l_config_file" | grep -Pio '^\h*([^#
]+|#\h*/[^#
\h]+\.conf )') if [ "${#A_out[@]}" -gt 0 ]; then f_parameter_chk else a_output2+=(" - No audit tool files are configured in an AIDE configuration file") fi else a_output2+=(" - AIDE configuration file not found." " Please verify AIDE is installed on the system") fi }
# Loop to only look at auditd tools that exist on the system for l_audit_file in "${a_audit_files[@]}"; do if [ -f "$(readlink -f "/sbin/$l_audit_file")" ]; then a_parlist+=("$l_audit_file") else a_output+=(" - Audit tool file "$(readlink -f "/sbin/$l_audit_file")" doesn't exist") fi done [ "${#a_parlist[@]}" -gt 0 ] && f_aide_conf
# Determine Pass/Fail state and provide output if [ "${#a_output2[@]}" -le 0 ]; then printf '%s
' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" [ "${#a_output3[@]}" -gt 0 ] && printf '%s
' "" " ** WARNING **" "${a_output3[@]}" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s
' "" "- Audit Result:" " ** FAIL **" " * Reasons for audit failure *" "${a_output2[@]}" "" [ "${#a_output3[@]}" -gt 0 ] && printf '%s
' "" " ** WARNING **" "${a_output3[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s
' "- Correctly set:" "${a_output[@]}" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 07/02/24 Check log files have appropriate access configured # E. Pinnell 08/14/24 Modified to fix error in function name on line 36 # Supersedes deprecated nix_logfile_access_chk.sh
a_output=(); a_output2=() f_file_test_chk() { a_out2=() maxperm="$( printf '%o' $(( 0777 & ~$perm_mask)) )" [ $(( $l_mode & $perm_mask )) -gt 0 ] && \ a_out2+=(" o Mode: \"$l_mode\" should be \"$maxperm\" or more restrictive") [[ ! "$l_user" =~ $l_auser ]] && \ a_out2+=(" o Owned by: \"$l_user\" and should be owned by \"${l_auser//|/ or }\"") [[ ! "$l_group" =~ $l_agroup ]] && \ a_out2+=(" o Group owned by: \"$l_group\" and should be group owned by \"${l_agroup//|/ or }\"") [ "${#a_out2[@]}" -gt 0 ] && a_output2+=(" - File: \"$l_fname\" is:" "${a_out2[@]}") } while IFS= read -r -d $'\0' l_file; do while IFS=: read -r l_fname l_mode l_user l_group; do if grep -Pq -- '\/(apt)\h*$' <<< "$(dirname "$l_fname")"; then perm_mask='0133' l_auser="root" l_agroup="(root|adm)"; f_file_test_chk else case "$(basename "$l_fname")" in lastlog | lastlog.* | wtmp | wtmp.* | wtmp-* | btmp | btmp.* | btmp-* | README) perm_mask='0113' l_auser="root" l_agroup="(root|utmp)" f_file_test_chk ;; cloud-init.log* | localmessages* | waagent.log*) perm_mask='0133' l_auser="(root|syslog)" l_agroup="(root|adm)" f_file_test_chk ;; secure{,*.*,.*,-*} | auth.log | syslog | messages) perm_mask='0137' l_auser="(root|syslog)" l_agroup="(root|adm)" f_file_test_chk ;; SSSD | sssd) perm_mask='0117' l_auser="(root|SSSD)" l_agroup="(root|SSSD)" f_file_test_chk ;; gdm | gdm3) perm_mask='0117' l_auser="root" l_agroup="(root|gdm|gdm3)" f_file_test_chk ;; *.journal | *.journal~) perm_mask='0137' l_auser="root" l_agroup="(root|systemd-journal)" f_file_test_chk ;; *) perm_mask='0137' l_auser="(root|syslog)" l_agroup="(root|adm)" if [ "$l_user" = "root" ] || ! grep -Pq -- "^\h*$(awk -F: '$1=="'"$l_user"'" {print $7}' /etc/passwd)\b" /etc/shells; then ! grep -Pq -- "$l_auser" <<< "$l_user" && l_auser="(root|syslog|$l_user)" ! grep -Pq -- "$l_agroup" <<< "$l_group" && l_agroup="(root|adm|$l_group)" fi f_file_test_chk ;; esac fi done < <(stat -Lc '%n:%#a:%U:%G' "$l_file") done < <(find -L /var/log -type f \( -perm /0137 -o ! -user root -o ! -group root \) -print0) if [ "${#a_output2[@]}" -le 0 ]; then a_output+=(" - All files in \"/var/log/\" have appropriate permissions and ownership") printf '\n%s' "- Audit Result:" " ** PASS **" "${a_output[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '\n%s' "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 03/11/24 check audit log files group owner # E. Pinnell 06/19/24 Modified to improve output
# XCCDF_VALUE_REGEX="(root|adm)" #<- example XCCDF_VALUE_REGEX variable
a_output=(); a_output2=(); a_output3=(); a_out2=(); l_count="0"
# Function to check if log_group agreement is configured correctly f_log_group_assigned_chk() { l_audit_log_group="$(awk -F= '/^\s*log_group\s*/{print $2}' /etc/audit/auditd.conf | xargs)" if grep -Pq -- "^\h*$l_group\b" <<< "$l_audit_log_group"; then a_output+=(" - Log file group correctly set to: \"$l_audit_log_group\" in \"/etc/audit/auditd.conf\"") else a_output2+=(" - Log file group is set to: \"$l_audit_log_group\" in \"/etc/audit/auditd.conf\"" " (should be set to group: \"root or adm\")") fi }
# Function check if auditd files in auditd log file directory for correct group ownership f_log_files_group_ownership_chk() { if [ -d "$l_audit_log_directory" ]; then l_group="$XCCDF_VALUE_REGEX" while IFS= read -r -d $'\0' l_file; do (( l_count++ )) l_file_group="$(stat -Lc '%G' "$l_file")" if ! grep -Pq -- '\b'"$l_group"'\b' <<< "$l_file_group"; then a_out2+=(" - File: \"$l_file\" is group owned by user: \"$l_file_group\"" " (should be group owned by user: \"${l_group/|/ or }\")") fi done < <(find "$l_audit_log_directory" -maxdepth 1 -type f -print0) if [ "$l_count" -gt "0" ]; then if [ "${#a_out2[@]}" -le 0 ]; then a_output+=(" - All files in: \"$l_audit_log_directory\" are group owned by group: \"${l_group/|/ or }\"") else a_output2+=("${a_out2[@]}") fi else a_output+=(" - No files exist in: \"$l_audit_log_directory\"") fi else a_output2+=(" - Log file directory not set in \"/etc/audit/auditd.conf\" please set log file directory") fi }
if [ -e "/etc/audit/auditd.conf" ]; then l_audit_log_directory="$(dirname "$(awk -F= '/^\s*log_file\s*/{print $2}' /etc/audit/auditd.conf | xargs)")" f_log_group_assigned_chk f_log_files_group_ownership_chk else a_output3+=(" - File: \"/etc/audit/auditd.conf\" not found." " ** Verify auditd is installed **") fi
# Send test results and output to CIS-CAT report if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" " ** WARNING **" "${a_output3[@]}" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " * Reasons for audit failure *" if [ "${#a_output2[@]}" -gt 50 ]; then printf '%s\n' " ** Note more that 50 files found with incorrect group ownership **" \ " Showing only the first 50" "${a_output2[@]:0:50}" else printf '%s\n' "${a_output2[@]}" fi [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" " ** WARNING **" "${a_output3[@]}" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" " * Correctly configured: *" "${a_output[@]}" exit "${XCCDF_RESULT_FAIL:-102}" fi
