ds:extended-component.sce:script:
#!/usr/bin/env bash
# # CIS-CAT Script Check Engine # # Name Date Description # ------------------------------------------------------------------- # E. Pinnell 11/14/24 Check sshd configuration parameter match
# Note: # - Replaces deprecated: sshd_running_config.sh, sshd_running_config_v2.sh nix_sshd_config_chk.sh, nix_sshd_config_not_match.sh # - Provides a much improved response time vs the now deprecated nix_sshd_config_chk.sh and nix_sshd_config_not_match.sh scripts # - Variable XCCDF_VALUE_REGEX: # - is a three part colon separated variable (Parameter: MaxStartups is a "special" case. See example below) # - is made up as "{OPTION}:{OPERATOR}:{OPTION_VALUE}" # - OPERATORS: # - match <- Pass if the set option value matches a string or regex in the {OPTION_VALUE} field # - not_match <- Pass if the set option value does not match a string or regex in the {OPTION_VALUE} field # - lt <- Pass if the set option value is less than the integer in the {OPTION_VALUE} field # - le <- Pass if the set option value is less than to equal to the integer in the {OPTION_VALUE} field # - gt <- Pass if the set option value is greater than the integer in the {OPTION_VALUE} field # - ge <- Pass if the set option value is greater than or equal to the integer in the {OPTION_VALUE} field # - Examples included below # XCCDF_VALUE_REGEX="MaxStartups::10:30:60" # <- example complex XCCDF_VALUE_REGEX variable # XCCDF_VALUE_REGEX="kexalgorithms:not_match:diffie-hellman-group1-sha1,diffie-hellman-group14-sha1,diffie-hellman-group-exchange-sha1,diffie-hellman-group-exchange-sha256" # XCCDF_VALUE_REGEX="allowusers,allowgroups,denyusers,denygroups:match:.+$" # XCCDF_VALUE_REGEX="banner:match:\/\H+"
# Initialize arrays and set variables a_output=(); a_output2=(); a_output3=() l_sshd_cmd="$(readlink -e /usr/sbin/sshd)" [ ! -f "$l_sshd_cmd" ] && l_sshd_cmd="$(whereis sshd | awk '{print $2}' | xargs)" [ ! -f "$l_sshd_cmd" ] && a_output2+=(" - The sshd command can not be found on the system") l_var="$("$l_sshd_cmd" -t 2>&1 | grep -Pvi -- '\bterminating\b')"
# Check Functions # Function to check string matching options f_regex_match() { while IFS= read -r l_match_option_name; do l_match_option="${l_match_option_name,,}" l_match_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_match_option"'$/) print tolower($2)}')" if [ -n "$l_match_config_value" ]; then if grep -Pqsi -- '\b('"$l_value"')\b' <<< "$l_match_config_value"; then a_output+=(" - Option: \"$l_match_option_name\" correctly set to: \"$l_match_config_value\"") else a_output2+=(" - Option(s): \"$l_match_option_name\" incorrectly set to: \"$l_match_config_value\"" \ " Should be set to: \"${l_value//|/ or }\"") fi fi done < <(echo -e "${l_option_name//,/\\n}") }
# Function to check string not match options (cyphers, MACs, key exchange, etc) f_regex_not_match() { a_wrong_value=(); while IFS= read -r l_wrong_value; do grep -Psiq -- '\b'"$l_wrong_value"'\b' <<< "$l_config_value" && a_wrong_value+=("$l_wrong_value") done < <(echo -e "${l_value//|/\\n}") if [ "${#a_wrong_value[@]}" -gt "0" ]; then l_wrong_out="${a_wrong_value[*]}" && a_output2+=(" - Option: \"$l_option_name\" incorrectly includes the following value(s):" \ " ${l_wrong_out// /, }") else a_output+=(" - Option: \"$l_option_name\" does not include and of the following incorrect values:" \ " ${l_value//|/, }") fi }
# Function to check integers f_integer_chk() { l_test="" case "$l_operator" in lt ) l_int_op_display="less than" [ "$l_config_value" -lt "$l_value" ] && l_test="pass" ;; le ) l_int_op_display="less than or equal to" [ "$l_config_value" -le "$l_value" ] && l_test="pass" ;; gt ) l_int_op_display="greater than" [ "$l_config_value" -gt "$l_value" ] && l_test="pass" ;; ge ) l_int_op_display="greater than or equal to" [ "$l_config_value" -ge "$l_value" ] && l_test="pass" ;; * ) l_int_op_display="equal to" [ "$l_config_value" = "$l_value" ] && l_test="pass" ;; esac if [ "$l_test" = "pass" ]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value\"" \ " Should be set to a value $l_int_op_display: \"${l_value//|/ or }\"") fi }
# Function to check "MaxStartups" option f_maxstartups_chk() { while IFS=: read -r _ _ l_value1 l_value2 l_value3; do while IFS=: read -r l_config_value1 l_config_value2 l_config_value3; do if [[ "$l_config_value1" -le "$l_value1" && "$l_config_value2" -le "$l_value2" && "$l_config_value3" -le "$l_value3" ]]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value1:$l_config_value2:$l_config_value3\"" \ " Should be set to: \"$l_value1:$l_value2:$l_value3\" or lower values") fi done <<< "$l_config_value" done <<< "$XCCDF_VALUE_REGEX" }
# Function to check "RekeyLimit" option f_rekeylimit_chk() { while IFS=: read -r _ _ l_value1 l_value2; do l_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_option"'$/) print tolower($2)":"tolower($3)}')" while IFS=: read -r l_config_value1 l_config_value2; do if [[ "$l_config_value1" -le "$l_value1" && "$l_config_value2" -le "$l_value2" ]]; then a_output+=(" - Option: \"$l_option_name\" correctly set to: \"$l_config_value1:$l_config_value2\"") else a_output2+=(" - Option: \"$l_option_name\" incorrectly set to: \"$l_config_value1:$l_config_value2\"" \ " Should be set to: \"$l_value1:$l_value2\" or lower values") fi done <<< "$l_config_value" done <<< "$XCCDF_VALUE_REGEX" }
# Main script if grep -Piq -- '\b(bad|unsupported)\b' <<< "$l_var"; then if sshd -T 2>&1 | grep -Pqsi '\bterminating\b'; then a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var" \ " - Unable to test configuration") else a_output2+=(" - openSSH server configuration includes bad and/or unsupported option(s):" " - $l_var") fi elif sshd -T 2>&1 | grep -Piq -- '\bMissing\h+privilege\h+separation\h+directory\b'; then a_output2+=(" - openSSH server hasn't fully started correctly" \ " Please run \"systemctl restart ssh\"" " and re-scan the system") else [ -n "$l_var" ] && a_output3+=(" ** WARNING **" " - $l_var") # Set hostname and hast address variables for sshd -T command if needed if sshd -T 2>&1 | grep -Pqi -- '\bmatch\h+group\b'; then l_hostname="$(hostname)" l_host_address="$(hostname -I | cut -d ' ' -f1)" l_sshd_command="$l_sshd_cmd -T -C user=root -C host=$l_hostname -C addr=$l_host_address" else l_sshd_command="$l_sshd_cmd -T" fi while IFS=: read -r l_option_name l_operator l_value; do l_option="${l_option_name,,}"; l_value="${l_value//,/|}" grep -q -- ',' <<< "$l_option" && l_option="(${l_option//,/|})" l_config_value="$($l_sshd_command | awk '{IGNORECASE=1; if ($1~/^\s*'"$l_option"'$/) print tolower($2)}')" if [ -n "$l_config_value" ]; then if [ "$l_option" = "maxstartups" ]; then f_maxstartups_chk elif [ "$l_option" = "rekeylimit" ]; then f_rekeylimit_chk else case "$l_operator" in match ) f_regex_match ;; not_match ) f_regex_not_match ;; * ) f_integer_chk ;; esac fi else a_output2+=(" - Parameter(s): \"${l_option_name//,/, }\"" " does not exist in the openSSH daemon configuration") fi done <<< "$XCCDF_VALUE_REGEX" fi
# Send check results and output to CIS-CAT if [ "${#a_output2[@]}" -le 0 ]; then printf '%s\n' "" "- Audit Result:" " ** PASS **" "${a_output[@]}" "" [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" "${a_output3[@]}" "" exit "${XCCDF_RESULT_PASS:-101}" else printf '%s\n' "" "- Audit Result:" " ** FAIL **" " - Reason(s) for audit failure:" "${a_output2[@]}" [ "${#a_output3[@]}" -gt 0 ] && printf '%s\n' "" "${a_output3[@]}" "" [ "${#a_output[@]}" -gt 0 ] && printf '%s\n' "" "- Correctly set:" "${a_output[@]}" "" exit "${XCCDF_RESULT_FAIL:-102}" fi

